import './App.css';
import axios from 'axios';
import React, {useState, useEffect} from 'react';

function App() {
  const [value,setValue] = useState();

  useEffect(() => {

    async function fetchData(){
      const apiData = await axios.get('https://api.covid19api.com/summary');
      let countries = await apiData.data.Countries;
      console.log(countries);
      let userValue = prompt('Enter Country');
      countries.filter((items,index)=>{
        let allCountry = items.Country;
        if (userValue.toUpperCase() === allCountry.toUpperCase()) {
          console.log('confirmed Cases ' + countries[index].NewConfirmed);
          console.log('confirmed deaths ' + countries[index].NewDeaths);
          console.log('confirmed recovered ' + countries[index].NewRecovered);

        }
      })

      // if (userValue.toUpperCase() === allCountry.toUpperCase()) {
