import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const [name,setName] = useState({
    fname: '',
    lname: ''
  });
  const getValue =(e)=>{
    let value = e.target.value;
    let name = e.target.name;

    setName((prevValue)=>{
      return{
        ...prevValue,
        [name]:value
      }
    })
  }
  return (
    <div>
      <form>
        <input value={name.fname} name="fname" onChange={getValue} type="text"/><br/>
        <input value={name.lname} name="lname" onChange={getValue} type="text"/>
      </form>
      <h1>{name.fname}{name.lname}</h1>
    </div>
  )
}    
export default App;
