import './App.css';
import axios from 'axios';
import React, {useState, useEffect} from 'react';

function App() {
  const [value,setValue] = useState();

  useEffect(() => {

    async function fetchData(){
      const apiData = await axios.get('https://api.covid19api.com/summary');
      let countries = await apiData.data.Countries;
      let userValue = prompt('Enter Country');
      countries.filter((items,index)=>{
        let allCountry = items.Country;
        if (userValue.toUpperCase() === allCountry.toUpperCase()) {
          console.log('confirmed Cases ' + countries[index].TotalConfirmed);
          console.log('New deaths ' + countries[index].TotalDeaths);
          console.log('Total recovered ' + countries[index].TotalRecovered);
        }
      })
    }
    fetchData();
  },[setValue]);

 const getCountry =(e)=>{
    let userValue = e.target.value;
    setValue(userValue);
}

  return (
    <div className="app">
      <input type="text" value={userValue} onChange={getCountry}/>

    </div>
    )
}
export default App;
