import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const[head,setHead] = useState(false);

  const change = ()=>{
    setHead(true);
  }
  const myStyle = {
    color:'red',
    fontSize : '3rem'
  }
  return (
    <div>
      <h1 style = {head ? myStyle: {}}>React</h1>
      <button onClick={change}>Click</button>
    </div>
  )
}    
export default App;
